import React, { useContext } from 'react'
import './ShopCatagory.css'
import { ShopContext } from '../componets/Context/ProductContext'
import sorticon from '../componets/assets/dropdown_icon.png'
import Item from '../componets/Items/Item'

const ShopCategory = (props) => {
  const  {allproduct} = useContext(ShopContext);
 console.log(props.category)
  return (
   
  <>
   
  <div className='Shopcatagory'>
      <img src={props.banner}></img>
    </div>
   <div className='sort'>
<p>
  <span>
    showing 1-12
  </span> out of 36 product
</p>
   </div>
   <div className='shop_sort'>
    <img src={sorticon} alt='non'/>
   </div>
   <div className='shop_product_sort'>
   {all_product.map((item,i)=>{
      if(props.category===item.category)
      {
        return(
          <Item key={i} id={item.id} name={item.name} image={item.image} new_price={item.new_price} old_price={item.old_price}  category={item.category}/>
        )
      }
      else{
        return null;
      }
    })}
   </div>
    </>
    
  )
}

export default ShopCategory
