const Getdefaultcart =()=>{
  let Cart={};
  for (let index = 0; index < all_product.length; index++) {
    Cart[index]=0;    
  }
  return Cart;
}


const ShopContextProvider = (props) => {
  const [cartitem,setcartitem]=useState(Getdefaultcart());
  const value = { all_product,cartitem}; 
 console.log(cartitem);

 

