import React, { useContext } from 'react'
import './Cart.css'
import removeicon from '../componets/assets/cart_cross_icon.png'

import { ShopContext } from '../componets/Context/ProductContext'
const Cart = () => {
const {all_product,cartitem,removefromcart}=useContext(ShopContext);
  return (
    <div className='cart_container'>
      <div className='cart_title'>
        <p>product</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
      </div>

{all_product.map((item)=>{
if(cartitem[item.id]>0)
  {
    return(
    <div className='cart_items'>
        <img className src={item.image} alt='img'/>
        <p>{item.name}</p>
        <p>{item.new_price}</p>
        <button className='Cart_Item_Quantity'>{cartitem[item.id]}</button>
        <p>{cartitem[item.id]}</p>
        <p>{item.new_price*cartitem[item.id]}</p>
        <img src={removeicon} onClick={()=>{removefromcart(item.id)}}/>
      </div>)
  }

})}     
    </div>
  )
}

export default Cart
