import React, { useContext } from 'react';
import './ShopCatagory.css';
import { ShopContext } from '../components/Context/ProductContext';
import sorticon from '../components/assets/dropdown_icon.png';
import Item from '../components/Items/Item';

const ShopCategory = ({ banner, category }) => {
  const { all_product } = useContext(ShopContext);

  if (!all_product) {
    return <div>Loading...</div>; // Handle loading state
  }

  const filteredProducts = all_product.filter(product => product.category === category);

  return (
    <>
      <div className='shop-category'>
        <img src={banner} alt='Category Banner' />
      </div>
      <div className='sort'>
        <p>
          <span>Showing 1-12</span> out of {filteredProducts.length} products
        </p>
      </div>
      <div className='shop-sort'>
        <img src={sorticon} alt='Sort Icon' />
      </div>
      <div className='shop-product-sort'>
        {filteredProducts.map((item, i) => (
          <Item
            key={i}
            id={item.id}
            name={item.name}
            image={item.image}
            new_price={item.new_price}
            old_price={item.old_price}
            category={item.category}
          />
        ))}
      </div>
    </>
  );
};

export default ShopCategory;
